import os 
from PIL import Image
from SistemaZEUS.sistema import Sistema

class edit_image:    
    def reduzir_tamanho(dir1, dir2):
        
        
        
        arquivo = os.listdir(dir1)
        nome = 'atual.png'

        #alteração de tamanho
        try:
            imagem = Image.open(os.path.join(dir1,nome))
        except:
            return False
        else:
            reduzido = imagem.resize((255, 200))
            reduzido.save(os.path.join(dir2,nome))
            return True
        
