from SistemaZEUS.conv_mp3 import conversao
import os
from pytube import YouTube, Playlist  
import requests


class Sistema:
    def remove_dir(caminho= ''):
        if caminho != '':
            dir = os.listdir(caminho)
           
            for file in dir:
                try:
                    os.remove(caminho+'/'+file)
                except:
                    pass
                

