import requests
def background(directory):
    link = input('link:' )
    img_list = []
    nome = 'tet'
    img_list.append(requests.get(link))

    for i, img in enumerate(img_list): #converte imagens para bytes
        with open(f'{directory}/{nome}_{i}.png', 'wb') as f: #wb = write byte
            f.write(img.content)

