import requests
from tqdm import tqdm 

def background(directory):
    chunk_size = 1024
    link = input('link:' )
    img_list = []
    nome = 'tet'
    r = requests.get(link, stream = True)
    total_size = int(r.headers['content-length'])

    #converte imagens para bytes
    with open(f'{directory}/{nome}.png', 'wb') as f: #wb = write byte
        for img in tqdm(iterable= r.iter_content(chunk_size= chunk_size), total=total_size/chunk_size, unit='KB'):
            f.write(img)






